function setupFormValidation(){const form=document.getElementById('cadastroForm');const message=document.getElementById('formMessage');form.addEventListener('submit',function(e){e.preventDefault();const nome=form.nome.value.trim();const email=form.email.value.trim();if(!nome||!email.includes('@')){message.textContent='Preencha corretamente todos os campos.';message.style.color='red'}else{message.textContent='Cadastro realizado com sucesso!';message.style.color='green';localStorage.setItem('cadastroData',JSON.stringify({nome,email}))}})}